// LoginPage.js
import React from 'react';
import { GoogleLogin } from 'react-google-login';
import { Link } from 'react-router-dom'; // Import this if you're using react-router-dom
// import FacebookLogin from 'react-facebook-login'; // Uncomment this when you have set up Facebook login

function LoginPage() {
  const responseGoogle = (response) => {
    console.log(response);
    // Send response to your backend to complete the authentication process
  };

  return (
    <div>
      <h1>Login Page</h1>
      <GoogleLogin
        clientId='your-google-client-id'
        buttonText='Login with Google'
        onSuccess={responseGoogle}
        onFailure={responseGoogle}
        cookiePolicy={'single_host_origin'}
      />
      {/* <FacebookLogin
        appId="your-facebook-app-id"
        autoLoad={false}
        fields="name,email,picture"
        callback={responseFacebook} /> */}{' '}
      // Uncomment this when you have set up Facebook login
      {/* Add your own login form here */}
      <Link to='/forgot-password'>Forgot Password?</Link> // Add this line
    </div>
  );
}

export default LoginPage;
